## Scope

Create `src/front/hooks/useTabManager.js`:

```javascript
/**
 * Manages tab state with localStorage persistence.
 * 
 * Features:
 * - Tab list state (open files)
 * - Active file tracking
 * - Active diff file tracking
 * - Automatic persistence to localStorage
 * - Restoration from localStorage
 * 
 * @param {Object} options
 * @param {string} options.storagePrefix - Prefix for localStorage keys
 * @param {boolean} options.shouldRestore - Whether to restore from storage
 * @returns {Object} Tab state and operations
 * 
 * @example
 * const {
 *   tabs,
 *   activeFile,
 *   activeDiffFile,
 *   addTab,
 *   removeTab,
 *   setActiveFile,
 *   setActiveDiffFile,
 * } = useTabManager({ storagePrefix: 'dockview', shouldRestore: layoutRestored });
 */
export function useTabManager({ storagePrefix, shouldRestore }) {
  const [tabs, setTabs] = useState([]);
  const [activeFile, setActiveFile] = useState(null);
  const [activeDiffFile, setActiveDiffFile] = useState(null);
  const restoredRef = useRef(false);

  // Restore tabs from localStorage
  useEffect(() => {
    if (!shouldRestore || restoredRef.current) return;

    const storageKey = \`\${storagePrefix}-tabs\`;
    const savedTabs = localStorage.getItem(storageKey);

    if (savedTabs) {
      try {
        const parsedTabs = JSON.parse(savedTabs);
        if (Array.isArray(parsedTabs)) {
          setTabs(parsedTabs);
        }
      } catch (e) {
        console.error('Failed to restore tabs:', e);
      }
    }

    restoredRef.current = true;
  }, [shouldRestore, storagePrefix]);

  // Persist tabs to localStorage
  useEffect(() => {
    // Only persist after initial restoration
    if (!restoredRef.current) return;

    const storageKey = \`\${storagePrefix}-tabs\`;
    if (tabs.length > 0) {
      localStorage.setItem(storageKey, JSON.stringify(tabs));
    } else {
      // Clear storage when no tabs
      localStorage.removeItem(storageKey);
    }
  }, [tabs, storagePrefix]);

  // Tab operations
  const addTab = useCallback((tab) => {
    setTabs(prev => {
      // Don't add duplicate tabs
      if (prev.some(t => t.path === tab.path)) {
        return prev;
      }
      return [...prev, tab];
    });
  }, []);

  const removeTab = useCallback((path) => {
    setTabs(prev => prev.filter(t => t.path !== path));
  }, []);

  const updateTab = useCallback((path, updates) => {
    setTabs(prev => prev.map(t => 
      t.path === path ? { ...t, ...updates } : t
    ));
  }, []);

  const clearTabs = useCallback(() => {
    setTabs([]);
    setActiveFile(null);
    setActiveDiffFile(null);
  }, []);

  return {
    // State
    tabs,
    activeFile,
    activeDiffFile,

    // Setters
    setTabs,
    setActiveFile,
    setActiveDiffFile,

    // Operations
    addTab,
    removeTab,
    updateTab,
    clearTabs,

    // Status
    isRestored: restoredRef.current,
  };
}
```

