## Scope

Create `src/front/hooks/useFileOperations.js`:

```javascript
/**
 * Provides file opening operations for the editor interface.
 * 
 * Operations:
 * - openFile: Smart positioning with fallback strategies
 * - openFileAtPosition: Opens at specific location in target group
 * - openFileToSide: Opens to the right of active editor
 * - openDiff: Opens file in diff view mode
 * 
 * @param {Object} options
 * @param {Object} options.dockApi - Dockview API
 * @param {Object} options.tabManager - From useTabManager
 * @param {string} options.projectRoot - Project root path
 * @param {Object} options.centerGroupRef - Ref to center editor group
 * @returns {Object} File operation callbacks
 * 
 * @example
 * const {
 *   openFile,
 *   openFileAtPosition,
 *   openFileToSide,
 *   openDiff,
 * } = useFileOperations({
 *   dockApi,
 *   tabManager,
 *   projectRoot,
 *   centerGroupRef,
 * });
 * 
 * // Open file with smart positioning
 * await openFile('/src/App.jsx', 42, 10);
 * 
 * // Open to the side
 * await openFileToSide('/src/utils.js');
 */
export function useFileOperations({
  dockApi,
  tabManager,
  projectRoot,
  centerGroupRef,
}) {
  const { tabs, setTabs, setActiveFile, setActiveDiffFile } = tabManager;

  // Core: Open file at specific position in specific group
  const openFileAtPosition = useCallback(async (filePath, line, column, groupId) => {
    if (!dockApi) return;

    // Normalize path
    const normalizedPath = normalizePath(filePath, projectRoot);
    const panelId = generateEditorPanelId(normalizedPath);

    // Check if already open
    const existingPanel = dockApi.getPanel(panelId);
    if (existingPanel) {
      existingPanel.api.setActive();
      // TODO: Navigate to line/column
      setActiveFile(normalizedPath);
      return;
    }

    // Fetch file content
    let content = '';
    try {
      const response = await fetch(\`/api/file?path=\${encodeURIComponent(filePath)}\`);
      if (response.ok) {
        const data = await response.json();
        content = data.content || '';
      }
    } catch (e) {
      console.error('Failed to fetch file:', e);
    }

    // Create panel
    const targetGroup = groupId ? dockApi.getGroup(groupId) : null;
    dockApi.addPanel({
      id: panelId,
      component: 'editor',
      title: getFileName(normalizedPath),
      params: { path: normalizedPath, content, line, column },
      position: targetGroup 
        ? { referenceGroup: targetGroup }
        : undefined,
    });

    // Update tab state
    setTabs(prev => {
      if (prev.some(t => t.path === normalizedPath)) return prev;
      return [...prev, { path: normalizedPath, title: getFileName(normalizedPath) }];
    });
    setActiveFile(normalizedPath);
  }, [dockApi, projectRoot, setTabs, setActiveFile]);

  // Smart positioning with fallbacks
  const openFile = useCallback(async (filePath, line, column) => {
    if (!dockApi) return;

    const { group, position } = findTargetGroupForFile(dockApi, centerGroupRef?.current);
    const groupId = group?.id;

    await openFileAtPosition(filePath, line, column, groupId);
  }, [dockApi, openFileAtPosition, centerGroupRef]);

  // Open to the right of active editor
  const openFileToSide = useCallback(async (filePath, line, column) => {
    if (!dockApi) return;

    const activePanel = dockApi.activePanel;
    const activeGroup = activePanel?.api?.group;

    // Create new group to the right
    const normalizedPath = normalizePath(filePath, projectRoot);
    const panelId = generateEditorPanelId(normalizedPath);

    let content = '';
    try {
      const response = await fetch(\`/api/file?path=\${encodeURIComponent(filePath)}\`);
      if (response.ok) {
        const data = await response.json();
        content = data.content || '';
      }
    } catch (e) {
      console.error('Failed to fetch file:', e);
    }

    dockApi.addPanel({
      id: panelId,
      component: 'editor',
      title: getFileName(normalizedPath),
      params: { path: normalizedPath, content, line, column },
      position: activeGroup
        ? { referenceGroup: activeGroup, direction: 'right' }
        : undefined,
    });

    setTabs(prev => {
      if (prev.some(t => t.path === normalizedPath)) return prev;
      return [...prev, { path: normalizedPath, title: getFileName(normalizedPath) }];
    });
    setActiveFile(normalizedPath);
  }, [dockApi, projectRoot, setTabs, setActiveFile]);

  // Open in diff view mode
  const openDiff = useCallback(async (filePath) => {
    if (!dockApi) return;

    const normalizedPath = normalizePath(filePath, projectRoot);
    const panelId = \`diff-\${normalizedPath.replace(/[^a-zA-Z0-9]/g, '-')}\`;

    // Fetch git diff content
    let diffContent = '';
    try {
      const response = await fetch(\`/api/git/diff?path=\${encodeURIComponent(filePath)}\`);
      if (response.ok) {
        const data = await response.json();
        diffContent = data.diff || '';
      }
    } catch (e) {
      console.error('Failed to fetch diff:', e);
    }

    dockApi.addPanel({
      id: panelId,
      component: 'diff',
      title: \`Diff: \${getFileName(normalizedPath)}\`,
      params: { path: normalizedPath, diff: diffContent },
    });

    setActiveDiffFile(normalizedPath);
  }, [dockApi, projectRoot, setActiveDiffFile]);

  return {
    openFile,
    openFileAtPosition,
    openFileToSide,
    openDiff,
  };
}

// Helper functions
function normalizePath(filePath, projectRoot) {
  if (projectRoot && filePath.startsWith(projectRoot)) {
    return filePath.slice(projectRoot.length).replace(/^\\//, '');
  }
  return filePath;
}

function generateEditorPanelId(path) {
  return \`editor-\${path.replace(/[^a-zA-Z0-9]/g, '-')}\`;
}

function getFileName(path) {
  return path.split('/').pop() || path;
}
```

