/**
 * Provides the onReady handler for DockviewReact initialization.
 * 
 * Responsibilities:
 * - Apply locked panel configurations
 * - Create core panel structure (filetree, terminal, shell, empty)
 * - Restore layout from localStorage
 * - Set up panel lifecycle handlers
 * - Configure layout save handlers with debouncing
 * 
 * @param {Object} options
 * @param {string} options.storagePrefix - Storage key prefix
 * @param {number} options.layoutVersion - Layout version for validation
 * @param {Object} options.panelDefaults - Default panel sizes
 * @param {Object} options.panelMinimums - Minimum panel sizes
 * @param {Object} options.refs - Refs object { centerGroup, layoutRestored, isInitialized }
 * @param {Function} options.setDockApi - Setter for dockApi state
 * @param {Function} options.setTabs - Setter for tabs state
 * @returns {Function} onReady callback for DockviewReact
 * 
 * @example
 * const onReady = useLayoutInit({
 *   storagePrefix,
 *   layoutVersion,
 *   panelDefaults,
 *   panelMinimums,
 *   refs,
 *   setDockApi,
 *   setTabs,
 * });
 * 
 * <DockviewReact onReady={onReady} ... />
 */
export function useLayoutInit({
  storagePrefix,
  layoutVersion,
  panelDefaults,
  panelMinimums,
  refs,
  setDockApi,
  setTabs,
}) {
  const onReady = useCallback((event) => {
    const api = event.api;
    setDockApi(api);

    // Apply locked panel configurations
    applyLockedPanels(api, CORE_PANEL_CONFIGS);

    // Try to restore layout from localStorage
    const savedLayout = loadLayout(storagePrefix, layoutVersion);

    if (savedLayout && validateLayout(savedLayout)) {
      // Restore saved layout
      api.fromJSON(savedLayout);
      refs.layoutRestored.current = true;
    } else {
      // Create fresh layout
      refs.centerGroup.current = ensureCorePanels(api, {
        panelDefaults,
        panelMinimums,
      });
    }

    // Apply initial panel sizes
    requestAnimationFrame(() => {
      const savedSizes = loadPanelSizes(storagePrefix);
      if (savedSizes) {
        applyPanelSizes(api, savedSizes, panelDefaults);
      }
      refs.isInitialized.current = true;
    });

    // Set up lifecycle handlers
    setupPanelLifecycleHandlers(api, {
      setTabs,
      refs,
      panelDefaults,
    });

    // Set up save handlers
    setupLayoutSaveHandlers(api, {
      storagePrefix,
      layoutVersion,
    });

  }, [storagePrefix, layoutVersion, panelDefaults, panelMinimums, refs, setDockApi, setTabs]);

  return onReady;
}

/**
 * Sets up panel add/remove handlers.
 */
function setupPanelLifecycleHandlers(api, { setTabs, refs, panelDefaults }) {
  // Handle panel removal (clean up tabs)
  api.onDidRemovePanel((event) => {
    const panelId = event.panel.id;

    // Clean up editor tabs
    if (panelId.startsWith('editor-')) {
      const path = panelId.replace('editor-', '').replace(/-/g, '/');
      setTabs(prev => prev.filter(t => t.path !== path));
    }

    // Restore empty panel if needed
    if (panelId === 'empty') {
      // Check if center area is empty
      const hasEditors = api.groups.some(g => 
        g.panels.some(p => p.id.startsWith('editor-'))
      );
      if (!hasEditors) {
        createEmptyPanel(api, refs.centerGroup.current);
      }
    }
  });

  // Handle panel add (for tracking)
  api.onDidAddPanel((event) => {
    // Optional: track panel additions
  });
}

/**
 * Sets up layout persistence with debouncing.
 */
function setupLayoutSaveHandlers(api, { storagePrefix, layoutVersion }) {
  // Debounced save functions
  const saveLayoutDebounced = debounce(() => {
    saveLayout(storagePrefix, api.toJSON(), layoutVersion);
  }, 300);

  const savePanelSizesDebounced = debounce(() => {
    savePanelSizes(storagePrefix, capturePanelSizes(api));
  }, 300);

  // Listen for layout changes
  api.onDidLayoutChange(() => {
    saveLayoutDebounced();
    savePanelSizesDebounced();
  });

  // Flush on page unload
  window.addEventListener('beforeunload', () => {
    saveLayoutDebounced.flush();
    savePanelSizesDebounced.flush();
  });
}

/**
 * Captures current panel sizes for persistence.
 */
function capturePanelSizes(api) {
  const sizes = {};

  const filetreeGroup = api.getGroup('filetree-group');
  if (filetreeGroup) sizes.filetree = filetreeGroup.width;

  const terminalGroup = api.getGroup('terminal-group');
  if (terminalGroup) sizes.terminal = terminalGroup.height;

  const shellGroup = api.getGroup('shell-group');
  if (shellGroup) sizes.shell = shellGroup.height;

  return sizes;
}

/**
 * Applies saved panel sizes.
 */
function applyPanelSizes(api, sizes, defaults) {
  const filetreeGroup = api.getGroup('filetree-group');
  if (filetreeGroup) {
    filetreeGroup.api.setSize({ width: sizes.filetree || defaults.filetree });
  }

  const terminalGroup = api.getGroup('terminal-group');
  if (terminalGroup) {
    terminalGroup.api.setSize({ height: sizes.terminal || defaults.terminal });
  }

  const shellGroup = api.getGroup('shell-group');
  if (shellGroup) {
    shellGroup.api.setSize({ height: sizes.shell || defaults.shell });
  }
}
