/**
 * Handles layout restoration after Dockview initialization.
 * 
 * This hook complements useLayoutInit by handling:
 * - Waiting for all dependencies (dockApi, projectRoot)
 * - Applying collapsed state after restoration
 * - Restoring saved panel sizes
 * - Tracking restoration status
 * 
 * Note: The actual layout JSON restoration happens in useLayoutInit's onReady.
 * This hook handles the follow-up tasks that depend on projectRoot.
 * 
 * @param {Object} options
 * @param {Object} options.dockApi - Dockview API
 * @param {string} options.projectRoot - Project root path
 * @param {string} options.storagePrefix - Storage key prefix
 * @param {Object} options.collapsed - Collapsed state
 * @param {Object} options.panelSizesRef - Ref to panel sizes
 * @param {Object} options.panelDefaults - Default panel sizes
 * @param {Object} options.panelMinimums - Minimum panel sizes
 * @param {Object} options.refs - Refs object
 * @returns {Object} { isRestored, isReady }
 */
export function useLayoutRestore({
  dockApi,
  projectRoot,
  storagePrefix,
  collapsed,
  panelSizesRef,
  panelDefaults,
  panelMinimums,
  refs,
}) {
  const [isRestored, setIsRestored] = useState(false);
  const [isReady, setIsReady] = useState(false);

  useEffect(() => {
    // Wait for dependencies
    if (!dockApi || !projectRoot) return;
    if (refs.layoutRestored.current) {
      setIsRestored(true);
      setIsReady(true);
      return;
    }

    const restore = async () => {
      try {
        // Ensure core panels exist (in case onReady didn't create them)
        if (!dockApi.getPanel('filetree')) {
          refs.centerGroup.current = ensureCorePanels(dockApi, {
            panelDefaults,
            panelMinimums,
          });
        }

        // Load and apply saved panel sizes
        const savedSizes = loadPanelSizes(storagePrefix);
        if (savedSizes) {
          panelSizesRef.current = { ...panelSizesRef.current, ...savedSizes };
        }

        // Apply initial panel sizes with requestAnimationFrame
        await new Promise(resolve => requestAnimationFrame(resolve));

        applyPanelSizes(dockApi, panelSizesRef.current, panelDefaults);

        // Prune empty groups
        pruneEmptyGroups(dockApi);

        // Mark as restored
        refs.layoutRestored.current = true;
        refs.isInitialized.current = true;
        setIsRestored(true);
        setIsReady(true);

      } catch (error) {
        console.error('Layout restoration failed:', error);
        // Attempt recovery by creating fresh layout
        refs.centerGroup.current = ensureCorePanels(dockApi, {
          panelDefaults,
          panelMinimums,
        });
        refs.layoutRestored.current = true;
        setIsRestored(true);
        setIsReady(true);
      }
    };

    restore();
  }, [dockApi, projectRoot, storagePrefix, collapsed, panelSizesRef, panelDefaults, panelMinimums, refs]);

  return { isRestored, isReady };
}

/**
 * Applies saved panel sizes to dockview groups.
 */
function applyPanelSizes(dockApi, sizes, defaults) {
  // Filetree (width)
  const filetreeGroup = dockApi.getGroup('filetree-group');
  if (filetreeGroup) {
    const width = sizes.filetree || defaults.filetree;
    filetreeGroup.api.setSize({ width });
  }

  // Terminal (height)
  const terminalGroup = dockApi.getGroup('terminal-group');
  if (terminalGroup) {
    const height = sizes.terminal || defaults.terminal;
    terminalGroup.api.setSize({ height });
  }

  // Shell (height)
  const shellGroup = dockApi.getGroup('shell-group');
  if (shellGroup) {
    const height = sizes.shell || defaults.shell;
    shellGroup.api.setSize({ height });
  }
}

/**
 * Removes empty groups from the layout.
 */
function pruneEmptyGroups(dockApi) {
  // Find groups with no panels
  const emptyGroups = dockApi.groups.filter(g => g.panels.length === 0);

  // Remove them (except protected groups)
  const protectedGroups = ['filetree-group', 'terminal-group', 'shell-group'];
  for (const group of emptyGroups) {
    if (!protectedGroups.includes(group.id)) {
      try {
        dockApi.removeGroup(group);
      } catch (e) {
        // Group may have been removed already
      }
    }
  }
}
