```javascript
/**
 * Synchronizes URL query parameters with editor state.
 * 
 * Features:
 * - Updates ?doc= parameter when active file changes
 * - Restores file from ?doc= on initial load
 * - Supports deep linking to specific files
 * 
 * @param {Object} options
 * @param {Object} options.dockApi - Dockview API
 * @param {boolean} options.isReady - Whether layout is ready for operations
 * @param {string} options.projectRoot - Project root (for validation)
 * @param {Function} options.openFile - File opening function
 * @param {string} options.queryParam - URL parameter name (default: 'doc')
 * 
 * @example
 * useUrlSync({
 *   dockApi,
 *   isReady: layoutRestored,
 *   projectRoot,
 *   openFile,
 * });
 * 
 * // URL: ?doc=src/App.jsx → opens src/App.jsx on load
 * // Active editor changes → URL updates to ?doc=src/utils.js
 */
export function useUrlSync({
  dockApi,
  isReady,
  projectRoot,
  openFile,
  queryParam = 'doc',
}) {
  const hasRestoredFromUrl = useRef(false);

  // Update URL when active panel changes
  useEffect(() => {
    if (!dockApi) return;

    const disposable = dockApi.onDidActivePanelChange((event) => {
      const panel = event?.panel;

      // Only track editor panels
      if (!panel?.id?.startsWith('editor-')) return;

      const path = panel.params?.path;
      if (!path) return;

      // Update URL without triggering navigation
      const url = new URL(window.location.href);
      url.searchParams.set(queryParam, path);
      window.history.replaceState({}, '', url);
    });

    return () => disposable.dispose();
  }, [dockApi, queryParam]);

  // Restore file from URL on initial load
  useEffect(() => {
    if (!isReady || !projectRoot || hasRestoredFromUrl.current) return;

    const url = new URL(window.location.href);
    const docPath = url.searchParams.get(queryParam);

    if (docPath) {
      // Open the file from URL
      openFile(docPath);
      hasRestoredFromUrl.current = true;
    }
  }, [isReady, projectRoot, openFile, queryParam]);
}

/**
 * Gets the current document path from URL.
 * 
 * @param {string} queryParam - URL parameter name
 * @returns {string|null} Document path or null
 */
export function getDocPathFromUrl(queryParam = 'doc') {
  const url = new URL(window.location.href);
  return url.searchParams.get(queryParam);
}

/**
 * Sets the document path in URL without navigation.
 * 
 * @param {string} path - Document path
 * @param {string} queryParam - URL parameter name
 */
export function setDocPathInUrl(path, queryParam = 'doc') {
  const url = new URL(window.location.href);
  if (path) {
    url.searchParams.set(queryParam, path);
  } else {
    url.searchParams.delete(queryParam);
  }
  window.history.replaceState({}, '', url);
}
```

