/**
 * Provides drag and drop handlers for the Dockview editor area.
 * 
 * Features:
 * - Detects droppable file data (custom mime type or standard Files)
 * - Parses dropped file data and opens at correct position
 * - Supports custom mime type 'application/x-boring-file'
 * 
 * @param {Object} options
 * @param {Function} options.openFileAtPosition - File opening function
 * @returns {Object} { showDndOverlay, onDidDrop }
 * 
 * @example
 * const { showDndOverlay, onDidDrop } = useDragDrop({
 *   openFileAtPosition,
 * });
 * 
 * <DockviewReact
 *   showDndOverlay={showDndOverlay}
 *   onDidDrop={onDidDrop}
 * />
 */
export function useDragDrop({ openFileAtPosition }) {
  /**
   * Determines if the drag event contains file data that should show overlay.
   * 
   * @param {DragEvent} event - Drag event from Dockview
   * @returns {boolean} Whether to show the drop overlay
   */
  const showDndOverlay = useCallback((event) => {
    const types = event.dataTransfer?.types || [];

    // Check for custom boring-ui file data
    if (types.includes(BORING_FILE_MIME_TYPE)) {
      return true;
    }

    // Check for standard file drag
    if (types.includes('Files')) {
      return true;
    }

    // Check for text/uri-list (dragged links)
    if (types.includes('text/uri-list')) {
      return true;
    }

    return false;
  }, []);

  /**
   * Handles the drop event and opens the file.
   * 
   * @param {Object} event - Dockview drop event with dataTransfer and position
   */
  const onDidDrop = useCallback((event) => {
    const { dataTransfer, position } = event;

    // Try custom mime type first
    const boringFileData = dataTransfer?.getData(BORING_FILE_MIME_TYPE);
    if (boringFileData) {
      handleBoringFileDrop(boringFileData, position, openFileAtPosition);
      return;
    }

    // Try text/uri-list (file:// URLs)
    const uriList = dataTransfer?.getData('text/uri-list');
    if (uriList) {
      handleUriListDrop(uriList, position, openFileAtPosition);
      return;
    }

    // Standard Files API handling could go here
    // (requires additional logic for reading file contents)

  }, [openFileAtPosition]);

  return { showDndOverlay, onDidDrop };
}

// Custom mime type for boring-ui file drags
export const BORING_FILE_MIME_TYPE = 'application/x-boring-file';

/**
 * Handles drop of custom boring-ui file data.
 */
function handleBoringFileDrop(data, position, openFileAtPosition) {
  try {
    const parsed = JSON.parse(data);

    if (!parsed.path) {
      console.warn('Dropped file data missing path');
      return;
    }

    openFileAtPosition(
      parsed.path,
      parsed.line || 1,
      parsed.column || 1,
      position?.groupId
    );

  } catch (e) {
    console.error('Failed to parse dropped file data:', e);
  }
}

/**
 * Handles drop of text/uri-list (file:// URLs).
 */
function handleUriListDrop(uriList, position, openFileAtPosition) {
  // Parse first URI from the list
  const firstUri = uriList.split('\n')[0]?.trim();

  if (!firstUri) return;

  // Handle file:// protocol
  if (firstUri.startsWith('file://')) {
    const path = decodeURIComponent(firstUri.replace('file://', ''));
    openFileAtPosition(path, 1, 1, position?.groupId);
    return;
  }

  // Could handle other protocols (http://, etc.) here
}

/**
 * Creates drag data for a file to be used in dragstart.
 * Use this when making file tree items draggable.
 * 
 * @param {Object} file - File object { path, line?, column? }
 * @returns {string} JSON string for dataTransfer.setData
 */
export function createBoringFileDragData(file) {
  return JSON.stringify({
    path: file.path,
    line: file.line || 1,
    column: file.column || 1,
  });
}
