  const [activePanel, setActivePanel] = useState(null);
  const [activePanelType, setActivePanelType] = useState(null);
  const [activePath, setActivePath] = useState(null);

  useEffect(() => {
    if (!dockApi) return;

    const handleActivePanelChange = (event) => {
      const panel = event?.panel;

      if (!panel) {
        setActivePanel(null);
        setActivePanelType(null);
        return;
      }

      setActivePanel(panel);

      // Determine panel type
      const panelType = getPanelType(panel.id);
      setActivePanelType(panelType);

      // Track editor path for file tree highlighting
      if (panelType === 'editor') {
        const path = panel.params?.path;
        if (path) {
          setActivePath(path);
          setActiveFile?.(path);
        }
      }
    };

    // Get initial state
    if (dockApi.activePanel) {
      handleActivePanelChange({ panel: dockApi.activePanel });
    }

    // Subscribe to changes
    const disposable = dockApi.onDidActivePanelChange(handleActivePanelChange);

    return () => disposable.dispose();
  }, [dockApi, setActiveFile]);

  return {
    activePanel,
    activePanelType,
    activePath,
  };
}

/**
 * Determines the type of panel from its ID.
 * 
 * @param {string} panelId - Panel ID
 * @returns {string} Panel type: 'editor' | 'filetree' | 'terminal' | 'shell' | 'review' | 'empty' | 'unknown'
 */
export function getPanelType(panelId) {
  if (!panelId) return 'unknown';

  if (panelId.startsWith('editor-')) return 'editor';
  if (panelId.startsWith('diff-')) return 'diff';
  if (panelId.startsWith('review-')) return 'review';
  if (panelId === 'filetree') return 'filetree';
  if (panelId === 'terminal') return 'terminal';
  if (panelId === 'shell') return 'shell';
  if (panelId === 'empty') return 'empty';

  return 'unknown';
}

/**
 * Checks if a panel ID represents an editor panel.
 * 
 * @param {string} panelId - Panel ID
 * @returns {boolean} True if editor panel
 */
export function isEditorPanel(panelId) {
  return panelId?.startsWith('editor-') || panelId?.startsWith('diff-');
}
