/**
 * Keeps panel parameters synchronized with current state and callbacks.
 * 
 * Panels receive their callbacks and state through Dockview's parameter system.
 * This hook ensures those parameters stay up-to-date when dependencies change.
 * 
 * @param {Object} options
 * @param {Object} options.dockApi - Dockview API
 * @param {Object} options.params - Parameters to sync by panel ID
 * 
 * @example
 * usePanelParams({
 *   dockApi,
 *   params: {
 *     filetree: { openFile, openFileToSide, toggleCollapse, projectRoot },
 *     terminal: { toggleCollapse, approvals, onFocusApproval },
 *     shell: { toggleCollapse },
 *   },
 * });
 */
export function usePanelParams({ dockApi, params }) {
  // Track previous params to detect changes
  const prevParamsRef = useRef({});

  useEffect(() => {
    if (!dockApi) return;

    // Update each panel's parameters
    for (const [panelId, panelParams] of Object.entries(params)) {
      const panel = dockApi.getPanel(panelId);
      if (!panel) continue;

      // Only update if params changed (shallow compare)
      const prevPanelParams = prevParamsRef.current[panelId];
      if (prevPanelParams && shallowEqual(prevPanelParams, panelParams)) {
        continue;
      }

      panel.api.updateParameters(panelParams);
      prevParamsRef.current[panelId] = panelParams;
    }
  }, [dockApi, params]);
}

/**
 * Hook for updating a single panel's parameters.
 * 
 * @param {Object} options
 * @param {Object} options.dockApi - Dockview API
 * @param {string} options.panelId - Panel ID to update
 * @param {Object} options.params - Parameters for the panel
 */
export function useSinglePanelParams({ dockApi, panelId, params }) {
  useEffect(() => {
    if (!dockApi) return;

    const panel = dockApi.getPanel(panelId);
    if (panel) {
      panel.api.updateParameters(params);
    }
  }, [dockApi, panelId, params]);
}

/**
 * Shallow equality check for parameter objects.
 */
function shallowEqual(a, b) {
  if (a === b) return true;
  if (!a || !b) return false;

  const keysA = Object.keys(a);
  const keysB = Object.keys(b);

  if (keysA.length !== keysB.length) return false;

  for (const key of keysA) {
    if (a[key] !== b[key]) return false;
  }

  return true;
}

/**
 * Creates a stable params object using useMemo.
 * Use this to prevent unnecessary updates.
 * 
 * @example
 * const filetreeParams = useStableParams({
 *   openFile,
 *   openFileToSide,
 *   toggleCollapse,
 *   projectRoot,
 * });
 */
export function useStableParams(params) {
  return useMemo(() => params, Object.values(params));
}
```

