  for (const panel of group.panels) {
    if (panel.id.startsWith('editor-')) {
      targetGroup = group;
      break;
    }
  }
  if (targetGroup) break;
}

// Fall back to centerGroupRef if no editor group
if (!targetGroup && centerGroupRef.current) {
  targetGroup = centerGroupRef.current;
}

// Fall back to empty panel's group
if (!targetGroup) {
  for (const group of dockApi.groups) {
    for (const panel of group.panels) {
      if (panel.id === 'empty') {
        targetGroup = group;
        break;
      }
    }
    if (targetGroup) break;
  }
}

// Fall back to shell group
if (!targetGroup) {
  targetGroup = dockApi.getGroup('shell-group');
  position = 'left';
}
