import React from 'react';

/**
 * Warning banner displayed when backend capabilities are missing.
 * 
 * States:
 * - Loading: Shows loading indicator while fetching capabilities
 * - Warning: Shows list of missing features
 * - Hidden: No issues detected
 * 
 * @param {Object} props
 * @param {boolean} props.isLoading - Whether capabilities are loading
 * @param {Object} props.capabilities - Capabilities response
 * @param {string} props.className - Additional CSS class
 * 
 * @example
 * const { capabilities, isLoading } = useCapabilities();
 * 
 * <CapabilityWarning
 *   isLoading={isLoading}
 *   capabilities={capabilities}
 * />
 */
export function CapabilityWarning({ isLoading, capabilities, className = '' }) {
  // Loading state
  if (isLoading) {
    return (
      <div className={\`capability-warning loading \${className}\`.trim()}>
        <LoadingSpinner size={14} />
        <span>Loading capabilities...</span>
      </div>
    );
  }

  // Check for missing features
  const missingFeatures = capabilities?.missingFeatures || [];
  const missingRouters = capabilities?.missingRouters || [];

  // No issues
  if (missingFeatures.length === 0 && missingRouters.length === 0) {
    return null;
  }

  // Build warning message
  const warnings = [];
  if (missingFeatures.length > 0) {
    warnings.push(\`Features: \${missingFeatures.join(', ')}\`);
  }
  if (missingRouters.length > 0) {
    warnings.push(\`APIs: \${missingRouters.join(', ')}\`);
  }

  return (
    <div 
      className={\`capability-warning \${className}\`.trim()}
      role="alert"
      aria-live="polite"
    >
      <WarningIcon size={16} />
      <span className="warning-text">
        Some features are unavailable: {warnings.join('; ')}
      </span>
      <button
        className="warning-dismiss"
        onClick={() => {/* Could add dismiss logic */}}
        aria-label="Dismiss warning"
      >
        <CloseIcon size={14} />
      </button>
    </div>
  );
}

