export default function App() {
  const config = useConfig();

  // Core state
  const {
    capabilities,
    capabilitiesLoading,
    dockApi,
    setDockApi,
    collapsed,
    setCollapsed,
    panelSizesRef,
    refs,
    storagePrefix,
    layoutVersion,
    panelDefaults,
    panelMinimums,
  } = useAppState();

  // Project metadata
  const { projectRoot, isLoading: projectLoading } = useProjectRoot();

  // Browser title
  useBrowserTitle(config);

  // Tab management
  const tabManager = useTabManager({
    storagePrefix,
    shouldRestore: refs.layoutRestored.current && projectRoot,
  });

  // Panel toggles
  const toggles = usePanelToggle({
    dockApi,
    collapsed,
    setCollapsed,
    panelSizesRef,
    storagePrefix,
    layoutVersion,
  });

  // Collapsed state effects
  useCollapsedState({
    dockApi,
    panelDefaults,
    panelMinimums,
    panelSizesRef,
    collapsed,
    setCollapsed,
  });

  // Approvals
  const {
    approvals,
    approvalsLoaded,
    handleDecision,
  } = useApprovals({ dockApi });

  // File operations
  const {
    openFile,
    openFileAtPosition,
    openFileToSide,
    openDiff,
  } = useFileOperations({
    dockApi,
    tabManager,
    projectRoot,
    centerGroupRef: refs.centerGroup,
  });

  // Approval panels
  useApprovalPanels({
    dockApi,
    approvals,
    approvalsLoaded,
    projectRoot,
    handleDecision,
    openFile,
    getReviewTitle: (a) => getReviewTitle(a, projectRoot),
  });

  // Layout initialization
  const onReady = useLayoutInit({
    storagePrefix,
    layoutVersion,
    panelDefaults,
    panelMinimums,
    refs,
    setDockApi,
    setTabs: tabManager.setTabs,
  });

  // Layout restoration
  const { isRestored } = useLayoutRestore({
    dockApi,
    projectRoot,
    storagePrefix,
    collapsed,
    panelSizesRef,
    panelDefaults,
    panelMinimums,
    refs,
  });

  // Active panel tracking
  const { activePath } = useActivePanel({
    dockApi,
    setActiveFile: tabManager.setActiveFile,
  });

  // URL synchronization
  useUrlSync({
    dockApi,
    isReady: isRestored,
    projectRoot,
    openFile,
  });

  // Drag and drop
  const { showDndOverlay, onDidDrop } = useDragDrop({
    openFileAtPosition,
  });

  // Keyboard shortcuts
  useKeyboardShortcuts({
    toggleFiletree: toggles.filetree,
    toggleTerminal: toggles.terminal,
    toggleShell: toggles.shell,
  });

  // Panel parameters
  usePanelParams({
    dockApi,
    params: {
      filetree: {
        openFile,
        openFileToSide,
        toggleCollapse: toggles.filetree,
        projectRoot,
        activePath,
