        },
      },
      shell: {
        toggleCollapse: toggles.shell,
      },
    },
  });

  // POC mode check
  const urlParams = new URLSearchParams(window.location.search);
  if (urlParams.get('poc') === 'chat') {
    return <POCChatView />;
  }

  // Main render
  return (
    <ThemeProvider>
      <div className="app-container">
        <AppHeader />

        <CapabilityWarning
          isLoading={capabilitiesLoading}
          capabilities={capabilities}
