 * - Collapsed panel states
 * - Panel size references
 * - Tab state
 * - Active file tracking
 * - Project root
 * 
 * @returns {Object} Application state and setters
 */
export function useAppState() {
  // Config
  const config = useConfig();
  const storagePrefix = config?.storage?.prefix || 'dockview';
  const layoutVersion = config?.storage?.layoutVersion || 1;
  const panelDefaults = config?.panels?.defaults || DEFAULT_PANEL_DEFAULTS;
  const panelMinimums = config?.panels?.minimums || DEFAULT_PANEL_MINIMUMS;

  // Capabilities
  const { capabilities, isLoading: capabilitiesLoading } = useCapabilities();

  // Dockview state
  const [dockApi, setDockApi] = useState(null);

  // Collapsed state
  const [collapsed, setCollapsed] = useState(INITIAL_COLLAPSED_STATE);
  const panelSizesRef = useRef(INITIAL_PANEL_SIZES);

  // File/tab state
  const [tabs, setTabs] = useState([]);
  const [activeFile, setActiveFile] = useState(null);
  const [activeDiffFile, setActiveDiffFile] = useState(null);

  // Approvals state
  const [approvals, setApprovals] = useState([]);
  const [approvalsLoaded, setApprovalsLoaded] = useState(false);

  // Project state
  const [projectRoot, setProjectRoot] = useState(null);

  // Refs for stable callbacks
  const refs = useMemo(() => ({
    projectRoot: useRef(null),
    storagePrefix: useRef(storagePrefix),
    collapsedEffectRan: useRef(false),
    centerGroup: useRef(null),
    layoutRestored: useRef(false),
    isInitialized: useRef(false),
