/**
 * Fetches and manages the project root path.
 * 
 * Features:
 * - Automatic fetch on mount
 * - Retry logic on failure (5s delay)
 * - Loading state tracking
 * - Error state tracking
 * 
 * @returns {Object} { projectRoot, isLoading, error, refetch }
 * 
 * @example
 * const { projectRoot, isLoading } = useProjectRoot();
 * if (isLoading) return <Loading />;
 * console.log('Project at:', projectRoot);
 */
export function useProjectRoot() {
  const [projectRoot, setProjectRoot] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const retryTimeoutRef = useRef(null);

  const fetchProjectRoot = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/project');
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}`);
      }
      const data = await response.json();
      setProjectRoot(data.root || null);
      setIsLoading(false);
    } catch (err) {
      console.error('Failed to fetch project root:', err);
      setError(err);
      // Retry after delay
      retryTimeoutRef.current = setTimeout(fetchProjectRoot, 5000);
    }
  }, []);

  useEffect(() => {
    fetchProjectRoot();
    return () => {
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
      }
    };
  }, [fetchProjectRoot]);

  return { projectRoot, isLoading, error, refetch: fetchProjectRoot };
}
```

