/**
 * Manages the browser document title based on configuration.
 * 
 * Priority:
 * 1. config.branding.titleFormat (dynamic format string)
 * 2. config.branding.title (static title)
 * 3. 'Boring UI' (default fallback)
 * 
 * @param {Object} config - Application config object
 * 
 * @example
 * const config = useConfig();
 * useBrowserTitle(config);
 * // Sets document.title based on config.branding
 */
export function useBrowserTitle(config) {
  useEffect(() => {
    const title = config?.branding?.titleFormat 
      || config?.branding?.title 
      || 'Boring UI';
    document.title = title;
  }, [config?.branding?.title, config?.branding?.titleFormat]);
}

/**
 * Alternative version that also supports dynamic title updates.
 * 
 * @param {Object} config - Application config
 * @param {string} suffix - Optional suffix (e.g., current file name)
 */
export function useDynamicBrowserTitle(config, suffix = null) {
  useEffect(() => {
    const baseTitle = config?.branding?.titleFormat 
      || config?.branding?.title 
      || 'Boring UI';

    document.title = suffix ? `${suffix} - ${baseTitle}` : baseTitle;
  }, [config?.branding?.title, config?.branding?.titleFormat, suffix]);
}
