/**
 * Configuration for a toggleable panel.
 * @typedef {Object} PanelToggleConfig
 * @property {string} groupId - Dockview group ID (e.g., 'filetree-group')
 * @property {string} stateKey - Key in collapsed state (e.g., 'filetree')
 * @property {'width'|'height'} dimension - Dimension to capture ('width' for vertical, 'height' for horizontal)
 */

/**
 * Creates toggle callbacks for collapsible panels.
 * 
 * Reduces boilerplate by generating toggle functions for multiple panels
 * from a declarative configuration.
 * 
 * @param {Object} options
 * @param {Object} options.dockApi - Dockview API instance
 * @param {Object} options.collapsed - Collapsed state object
 * @param {Function} options.setCollapsed - State setter
 * @param {Object} options.panelSizesRef - Ref to panel sizes
 * @param {string} options.storagePrefix - Storage key prefix
 * @param {number} options.layoutVersion - Layout version
 * @param {PanelToggleConfig[]} options.panels - Panel configurations
 * @returns {Object} Object with toggle functions keyed by stateKey
 * 
 * @example
 * const toggles = usePanelToggle({
 *   dockApi,
 *   collapsed,
 *   setCollapsed,
 *   panelSizesRef,
 *   storagePrefix,
 *   layoutVersion,
 *   panels: [
 *     { groupId: 'filetree-group', stateKey: 'filetree', dimension: 'width' },
 *     { groupId: 'terminal-group', stateKey: 'terminal', dimension: 'height' },
 *     { groupId: 'shell-group', stateKey: 'shell', dimension: 'height' },
 *   ],
 * });
 * 
 * // Use: toggles.filetree(), toggles.terminal(), toggles.shell()
 */
export function usePanelToggle({
  dockApi,
  collapsed,
  setCollapsed,
  panelSizesRef,
  storagePrefix,
  layoutVersion,
  panels,
}) {
  return useMemo(() => {
    const toggles = {};

    for (const { groupId, stateKey, dimension } of panels) {
      toggles[stateKey] = () => {
        const group = dockApi?.getGroup(groupId);
        if (!group) return;

        const currentSize = dimension === 'width' ? group.width : group.height;

        if (!collapsed[stateKey]) {
          // Capture size before collapsing
          panelSizesRef.current[stateKey] = currentSize;
          savePanelSizes(storagePrefix, panelSizesRef.current, layoutVersion);
        }

        setCollapsed(prev => ({ ...prev, [stateKey]: !prev[stateKey] }));
      };
    }

    return toggles;
  }, [dockApi, collapsed, setCollapsed, panelSizesRef, storagePrefix, layoutVersion, panels]);
}

// Default panel configurations matching App.jsx
export const DEFAULT_PANEL_TOGGLES = [
  { groupId: 'filetree-group', stateKey: 'filetree', dimension: 'width' },
  { groupId: 'terminal-group', stateKey: 'terminal', dimension: 'height' },
  { groupId: 'shell-group', stateKey: 'shell', dimension: 'height' },
];
```

