 * @param {Object} options.panelSizesRef - Ref to saved panel sizes
 * @returns {Object} { collapsed, setCollapsed, isEffectReady }
 */
export function useCollapsedState({
  dockApi,
  panelDefaults,
  panelMinimums,
  panelSizesRef,
}) {
  const [collapsed, setCollapsed] = useState({
    filetree: false,
    terminal: false,
    shell: false,
  });

  const effectRan = useRef(false);

  // Apply collapsed state to Dockview
  useEffect(() => {
    if (!dockApi) return;

    // Skip first run to let layout restoration happen
    if (!effectRan.current) {
      effectRan.current = true;
      return;
    }

    applyCollapsedState(dockApi, collapsed, panelSizesRef, panelDefaults, panelMinimums);
  }, [dockApi, collapsed, panelDefaults, panelMinimums, panelSizesRef]);

  return { collapsed, setCollapsed, isEffectReady: effectRan.current };
}

/**
 * Applies collapsed state to a specific panel group.
 * 
 * @param {Object} group - Dockview group
 * @param {boolean} isCollapsed - Whether the panel is collapsed
 * @param {number} savedSize - Saved size to restore when expanding
 * @param {number} minSize - Minimum size constraint
 * @param {'width'|'height'} dimension - Dimension to apply
 */
function applyPanelCollapsedState(group, isCollapsed, savedSize, minSize, dimension) {
  if (!group) return;

  if (isCollapsed) {
    group.api.setConstraints({ minimumSize: 0, maximumSize: 0 });
    group.api.setSize({ [dimension]: 0 });
  } else {
    group.api.setConstraints({ minimumSize: minSize, maximumSize: Infinity });
    group.api.setSize({ [dimension]: savedSize });
  }
}

/**
 * Applies collapsed state to all configured panels.
 */
function applyCollapsedState(dockApi, collapsed, panelSizesRef, panelDefaults, panelMinimums) {
  // Filetree (width)
  const filetreeGroup = dockApi.getGroup('filetree-group');
  const filetreeSize = panelSizesRef.current.filetree || panelDefaults.filetree;
  applyPanelCollapsedState(filetreeGroup, collapsed.filetree, filetreeSize, panelMinimums.filetree, 'width');

  // Terminal (height)
  const terminalGroup = dockApi.getGroup('terminal-group');
  const terminalSize = panelSizesRef.current.terminal || panelDefaults.terminal;
  applyPanelCollapsedState(terminalGroup, collapsed.terminal, terminalSize, panelMinimums.terminal, 'height');

  // Shell (height)
  const shellGroup = dockApi.getGroup('shell-group');
  const shellSize = panelSizesRef.current.shell || panelDefaults.shell;
  applyPanelCollapsedState(shellGroup, collapsed.shell, shellSize, panelMinimums.shell, 'height');
}
