```python
"""Storage abstraction for file operations."""
from abc import ABC, abstractmethod
from pathlib import Path
from typing import Any
import os

class Storage(ABC):
    """Abstract storage interface.

    Implementations handle file I/O for different backends.
    All paths are relative to the workspace root.
    """

    @abstractmethod
    def list_dir(self, path: Path) -> list[dict[str, Any]]:
        """List directory contents."""
        ...

    @abstractmethod
    def read_file(self, path: Path) -> str:
        """Read file contents as string."""
        ...

    @abstractmethod
    def write_file(self, path: Path, content: str) -> None:
        """Write content to file."""
        ...

    @abstractmethod
    def delete(self, path: Path) -> None:
        """Delete file or directory."""
        ...

    @abstractmethod
    def rename(self, old_path: Path, new_path: Path) -> None:
        """Rename/move a file."""
        ...

    @abstractmethod
    def exists(self, path: Path) -> bool:
        """Check if path exists."""
        ...


class LocalStorage(Storage):
    """Local filesystem storage implementation."""

    def __init__(self, root: Path):
        self.root = root.resolve()

    def _resolve(self, path: Path) -> Path:
        return self.root / path

    def list_dir(self, path: Path) -> list[dict[str, Any]]:
        full_path = self._resolve(path)
        entries = []
        for entry in os.scandir(full_path):
            entries.append({
                'name': entry.name,
                'path': str(Path(path) / entry.name),
                'is_dir': entry.is_dir(),
                'size': entry.stat().st_size if entry.is_file() else 0,
            })
        return sorted(entries, key=lambda e: (not e['is_dir'], e['name'].lower()))

    # ... (implement other methods)


class S3Storage(Storage):
    """AWS S3 storage implementation (optional)."""
    # Only needed if S3 support is required
    pass
